mapper.update:
update b_message set status = 1 where is_deleted = 0
and id in

    #{id}

mapper.select:
SELECT bm.*, bu.username AS fromUserName, bu2.username AS toUserName FROM b_message bm LEFT JOIN b_user bu ON bm.from_user_id = bu.id AND bu.is_deleted = 0 LEFT JOIN b_user bu2 on bm.to_user_id = bu2.id AND bu.is_deleted = 0 WHERE bm.is_deleted = 0
and bm.to_user_id = #{userId}
order by bm.gmt_create DESC
